function sendFriendRequest(e){let n=$(e).data("to");$.ajax({type:"get",url:"/friend/add",data:{to:$(e).data("to")},success:function(e){console.log("data"),$(`#user-${n}`).remove()},error:function(e){console.log("error")}})}function acceptFriendRequest(e){let n=$(e).data("id"),t=$(e).data("to"),a=$(e).data("name"),o=addFriendDOM(t,a);$("#friends-list").prepend(o),$.ajax({type:"get",url:"/friend/accept",data:{to:$(e).data("to"),id:$(e).data("id")},success:function(e){console.log("data"),$(`#name-${n}`).remove(),$(`#button-${n}`).remove()},error:function(e){console.log("error")}})}let addFriendDOM=function(e,n){return $(`\n     <li>\n        <div class="username">\n            <a href="/user/profile/${e}">\n                ${n}\n            </a>\n        </div>\n         </li>\n    `)};